class Solution:
    def longestPalindrome(self, s: str) -> str:

        i = 0
        x = len(s)
        # k = j +1
        # pall = []
        max_str = ""
        # print(len(max_str))
        if x == 1:
            return s[0]
        # if x == 2 and s == s[::-1]:
        #     return s
        for i in range(x):
            for j in range(x):
                # if j != 0:
                sub_str = s[i: j + 1]
                sub_str2 = sub_str[:: -1]
                if sub_str == sub_str2 and sub_str != "" and len(sub_str) >= len(max_str):
                    max_str = sub_str
        return max_str

if __name__ == "__main__":
    s = Solution()
    str_1 = ""
    print(s.longestPalindrome(str_1))


